import { HttpClient } from "@angular/common/http";
import { Component, Inject, OnInit } from "@angular/core";
import { FormGroup, FormBuilder, Validators } from "@angular/forms";
import { Router } from "@angular/router";
import { ToastrService } from "ngx-toastr";
import { DataService } from "../service/data.service";
import { User } from "datatype";

@Component({
  selector: "app-login",
  templateUrl: "./login.component.html",
  styleUrls: ["./login.component.css"],
})
export class LoginComponent implements OnInit {
  public Form!: FormGroup;
  constructor(
    private formBuilder: FormBuilder,
    private http: HttpClient,
    public router: Router,
    public toastrService: ToastrService,
    public data: DataService
  ) {}
  ngOnInit(): void {
    this.Form = this.formBuilder.group({
      email: ["", Validators.required, Validators.email],
      password: [
        "",
        Validators.pattern(
          /^(?=.*[A-Z])(?=.*[a-z])(?=.*[0-9])(?=.*[!@#\$%\^&\*])(?=.{8,})/
        ),
      ],
    });
  }

  onSubmit() {
    this.data.getSignupusers(this.Form.value).subscribe(
      (res: any) => {
        if (res.role === "Admin") {
          localStorage.setItem("Admin", res.accessToken);
          this.toastrService.success("welcome admin");
          this.router.navigate(["/admin"]);
        } else {
          this.toastrService.success("you re successfully logged in");
          localStorage.setItem("user", res.accessToken);
          this.Form.reset();
          sessionStorage.setItem("user",this.Form.value.email)
          this.router.navigate(["home"]);
        }
      },
      (error) => {
        this.toastrService.error('invalid user ');
      }
    );
  }
}
<div class="body">
  <div class="heading">
    <h1>Please login to continue!!!</h1>
  </div>
  <div class="container">
    <div class="screen">
      <div class="screen__content">
        <form class="login" [formGroup]="Form" (ngSubmit)="onSubmit()">
          <div class="login__field">
            <input
              type="text"
              class="login__input"
              placeholder="Username"
              formControlName="email"
              name="email"
            />
          </div>
          <div class="login__field">
            <i class="login__icon fas fa-lock"></i>
            <input
              type="password"
              class="login__input"
              placeholder="Password"
              formControlName="password"
              name="password"
            />
            <span
              id="c"
              *ngIf="
                Form.get('password')?.invalid && Form.get('password')?.touched
              "
              >Password invalid</span
            >
          </div>
          <button class="button login__submit" [disabled]="Form.invalid">
            <span routerLink="/products" class="button__text">Log In Now</span>
            <i class="button__icon fas fa-chevron-right"></i>
          </button>
        </form>
      </div>
      <br />
      <a routerLink="/signup">New user? click to Signup</a>
      <div class="screen__background">
        <span
          class="screen__background__shape screen__background__shape4"
        ></span>
        <span
          class="screen__background__shape screen__background__shape3"
        ></span>
        <span
          class="screen__background__shape screen__background__shape2"
        ></span>
        <span
          class="screen__background__shape screen__background__shape1"
        ></span>
      </div>
    </div>
  </div>
</div>
<router-outlet></router-outlet>
