import { Component, OnChanges, OnInit,ViewChild } from "@angular/core";
import { Router } from "@angular/router";
import { products } from "datatype";
import { ToastrService } from "ngx-toastr";
import { AdminService } from "../service/admin.service";
import { MatDialog } from "@angular/material/dialog";
import { UpdateComponent } from "../update/update.component";
import { CartService } from "../service/cart.service";



@Component({
  selector: "app-view-edit-product",
  templateUrl: "./view-edit-product.component.html",
  styleUrls: ["./view-edit-product.component.css"],
})
export class ViewEditProductComponent implements OnInit {
  public items!: products[];
  constructor(
    private admin: AdminService,
    private router: Router,
    private toastrService: ToastrService,
    public dialog: MatDialog,
    private cart: CartService,
    // @ViewChild('paginator') paginator:MatPaginator
  ) {}
  ngOnInit(): void {
    this.getProducts();
  }
  getProducts() {
    this.admin.getProducts().subscribe((res) => {
      this.items = res;
    });
  }
  onSubmit(deleteproduct: number) {
    this.admin.deleteProducts(deleteproduct).subscribe(
      (res) => {
        this.toastrService.info("product is deleted");
        this.getProducts();
      },
      (error) => {
        this.toastrService.error(`${error.name} error ${error.status}`);
      }
    );
    this.getProducts();
  }
  updateSubmit(data: any) {
    localStorage.setItem("id", data);
    this.dialog
      .open(UpdateComponent)
      .afterClosed()
      .subscribe(() => {
        this.getProducts();
      });
  }
}
<app-header></app-header>

<body id="class">
  <div>
    <button class="back" routerLink="/admin">back</button>
  </div>
  <div class="container">
    <div class="card-table">
      <div class="cart-product">
        <table class="table table-responsive">
          <thead>
            <tr>
              <th>No</th>
              <th>Item name</th>
              <th>Item image</th>
              <th>price</th>
              <th>quantity</th>
              <th>category</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let item of items; let i = index">
              <td>{{ i + 1 }}</td>
              <td>{{ item.title }}</td>
              <td>
                <img
                  style="width: 100px; height: 80px"
                  src="{{ item.image }}"
                  alt=""
                />
              </td>
              <td>{{ item.price }}</td>
              <td>{{ item.quantity }}</td>
              <td>{{ item.total }}</td>
              <td>{{ item.category }}</td>
              <td>
                <button
                  (click)="updateSubmit(item._id)"
                  class="btn btn-primary"
                >
                  update
                </button>
              </td>
              <td>
                <button (click)="onSubmit(item._id)" class="btn btn-danger">
                  remove
                </button>
              </td>
            </tr>
          </tbody>
        </table>
        <mat-paginator #paginator [length]="100" [pageSizeOptions]="[3,5,10]" aria-label="select page">

        </mat-paginator>
      </div>
    </div>
  </div>
</body>
